#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MAX_N 100
#define MAX_A 10000

double func(double x, double r, double v, double e, double f) {
    if (x >= r) {
        return 1 / (v - e * (x - r));
    } else {
        return 1 / (v - f * (r - x));
    }
}

double dp[MAX_N + 1][MAX_N + 1];

double solve(int n, int a[], double b, int r, double v, double e, double f) {
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            dp[i][j] = 0;
        }
    }

    for (int i = 0; i <= n; i++) {
        dp[i][i] = func(a[i] - (i == 0 ? 0 : a[i - 1]), r, v, e, f);
    }

    for (int len = 1; len <= n; len++) {
        for (int i = 0; i + len <= n; i++) {
            int j = i + len;
            double min = 1e100;
            for (int k = i; k <= j; k++) {
                double time = dp[i][k] + dp[k][j] + b;
                if (time < min) {
                    min = time;
                }
            }
            dp[i][j] = min;
        }
    }

    return dp[0][n];
}

int main() {
    int n;
    double b, r, v, e, f;
    int a[MAX_N];

    while (scanf("%d", &n) == 1) {
        if (n == 0) {
            break;
        }

        for (int i = 0; i < n; i++) {
            scanf("%d", &a[i]);
        }

        scanf("%lf", &b);
        scanf("%d%lf%lf%lf%lf", &r, &v, &e, &f);

        double result = solve(n, a, b, r, v, e, f);
        printf("%.4lf\n", result);

        for (int i = 0; i < n; i++) {
            a[i] = 0;
        }
    }

    return 0;
}
