#include <iostream>
#include <vector>
#include <cmath>
#include <limits>

using namespace std;

double f(double x, double r, double v, double e, double f) {
    if (x >= r) {
        return 1 / (v - e * (x - r));
    } else {
        return 1 / (v - f * (r - x));
    }
}

double dp(vector<double>& a, double b, double r, double v, double e, double f) {
    int n = a.size();
    vector<double> dp(n + 1, numeric_limits<double>::infinity());
    dp[0] = 0;

    for (int i = 1; i <= n; i++) {
        double x = a[i - 1] - a[i - 2];
        double t = f(x, r, v, e, f) * a[i - 1] + b;
        dp[i] = min(dp[i], dp[i - 1] + t);
        dp[i] = min(dp[i], dp[i - 1] + a[i - 1] / (v - e * (a[i - 1] - r)) + b);
    }

    return dp[n];
}

int main() {
    double n;
    while (cin >> n) {
        if (n == 0) break;
        vector<double> a(n);
        for (int i = 0; i < n; i++) {
            cin >> a[i];
        }
        double b;
        cin >> b;
        double r, v, e, f;
        cin >> r >> v >> e >> f;
        cout << fixed << setprecision(10) << dp(a, b, r, v, e, f) << endl;
    }
    return 0;
}
