import sys

def calculate_time(a, b, r, v, e, f):
    n = len(a)
    dp = [[0.0 for _ in range(n)] for _ in range(n)]
    for i in range(n):
        dp[i][i] = 0
    for length in range(2, n + 1):
        for i in range(n - length + 1):
            j = i + length - 1
            dp[i][j] = float('inf')
            for k in range(i, j):
                time = dp[i][k] + dp[k + 1][j] + b
                if k == i:
                    time += 1 / (v - e * (a[k + 1] - a[k]))
                else:
                    time += 1 / (v - f * (a[k] - a[k + 1]))
                dp[i][j] = min(dp[i][j], time)
    return dp[0][n - 1]

def main():
    while True:
        n = int(input())
        if n == 0:
            break
        a = list(map(int, input().split()))
        b = float(input())
        r, v, e, f = map(float, input().split())
        print(f"{calculate_time(a, b, r, v, e, f):.10f}")

if __name__ == "__main__":
    main()
