#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int MAXN = 55;

int n;
vector<vector<int>> dist;
vector<int> parent;
vector<int> degree;

void dfs(int u) {
    for (int v = 0; v < n; v++) {
        if (v != u && dist[u][v] == 1) {
            parent[v] = u;
            degree[u]++;
            dfs(v);
        }
    }
}

int main() {
    int t;
    cin >> t;
    while (t--) {
        cin >> n;
        dist.resize(n);
        for (int i = 0; i < n; i++) {
            dist[i].resize(n);
            for (int j = 0; j < n; j++) {
                cin >> dist[i][j];
            }
        }
        parent.resize(n);
        degree.resize(n);
        for (int i = 0; i < n; i++) {
            if (parent[i] == 0) {
                dfs(i);
            }
        }
        sort(degree.begin(), degree.end());
        for (int i = 0; i < n; i++) {
            if (degree[i] != 0) {
                cout << degree[i] << " ";
            }
        }
        cout << endl;
    }
    return 0;
}
