import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.StringTokenizer;

public class Main {
    static int N;
    static int[][] dist;
    static int[][] parent;
    static int[] degree;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String line;
        while ((line = br.readLine()) != null && !line.equals("0")) {
            StringTokenizer st = new StringTokenizer(line);
            N = Integer.parseInt(st.nextToken());
            dist = new int[N][N];
            for (int i = 0; i < N; i++) {
                st = new StringTokenizer(br.readLine());
                for (int j = 0; j < N; j++) {
                    dist[i][j] = Integer.parseInt(st.nextToken());
                }
            }
            parent = new int[N][N];
            degree = new int[N];
            Arrays.fill(degree, 1);
            for (int i = 0; i < N; i++) {
                for (int j = i + 1; j < N; j++) {
                    if (dist[i][j] == 1) {
                        parent[i][j] = parent[j][i] = i;
                    }
                }
            }
            for (int k = 0; k < N; k++) {
                for (int i = 0; i < N; i++) {
                    for (int j = i + 1; j < N; j++) {
                        if (dist[i][k] + dist[k][j] == dist[i][j]) {
                            parent[i][j] = parent[j][i] = k;
                            degree[k]++;
                        }
                    }
                }
            }
            for (int i = 0; i < N; i++) {
                for (int j = i + 1; j < N; j++) {
                    if (parent[i][j] == 0) {
                        parent[i][j] = i;
                    }
                }
            }
            for (int i = 0; i < N; i++) {
                for (int j = i + 1; j < N; j++) {
                    if (parent[i][j] == i) {
                        degree[i]++;
                    }
                }
            }
            Arrays.sort(degree);
            for (int i = 0; i < N; i++) {
                if (degree[i] > 1) {
                    System.out.print(degree[i] + " ");
                }
            }
            System.out.println();
        }
    }
}
