import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.*;

public class Main {
    static class BingoCard {
        int[][] numbers;
        int size;

        public BingoCard(int[][] numbers, int size) {
            this.numbers = numbers;
            this.size = size;
        }

        public boolean checkBingo(int[] sequence) {
            for (int i = 0; i < size; i++) {
                boolean bingo = true;
                for (int j = 0; j < size; j++) {
                    if (numbers[i][j] != sequence[j]) {
                        bingo = false;
                        break;
                    }
                }
                if (bingo) {
                    return true;
                }
            }

            for (int j = 0; j < size; j++) {
                boolean bingo = true;
                for (int i = 0; i < size; i++) {
                    if (numbers[i][j] != sequence[i]) {
                        bingo = false;
                        break;
                    }
                }
                if (bingo) {
                    return true;
                }
            }

            boolean bingo = true;
            for (int i = 0; i < size; i++) {
                if (numbers[i][i] != sequence[i]) {
                    bingo = false;
                    break;
                }
            }
            if (bingo) {
                return true;
            }

            bingo = true;
            for (int i = 0; i < size; i++) {
                if (numbers[i][size - i - 1] != sequence[i]) {
                    bingo = false;
                    break;
                }
            }
            return bingo;
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String[] input;
        while ((input = br.readLine().split(" ")) != null && input.length > 0) {
            if (input[0].equals("0") && input[1].equals("0")) {
                break;
            }
            int p = Integer.parseInt(input[0]);
            int m = Integer.parseInt(input[1]);
            BingoCard[] cards = new BingoCard[p];
            for (int i = 0; i < p; i++) {
                input = br.readLine().split(" ");
                int[][] numbers = new int[m][m];
                for (int j = 0; j < m; j++) {
                    for (int k = 0; k < m; k++) {
                        numbers[j][k] = Integer.parseInt(input[j * m + k]);
                    }
                }
                cards[i] = new BingoCard(numbers, m);
            }

            int min = Integer.MAX_VALUE;
            for (int i = 0; i < (1 << (p * m * m)); i++) {
                int[] sequence = new int[p * m];
                int index = 0;
                for (int j = 0; j < p; j++) {
                    for (int k = 0; k < m; k++) {
                        for (int l = 0; l < m; l++) {
                            if ((i & (1 << (j * m * m + k * m + l))) != 0) {
                                sequence[index++] = cards[j].numbers[k][l];
                            }
                        }
                    }
                }
                boolean valid = true;
                for (int j = 0; j < p; j++) {
                    for (int k = 0; k < p; k++) {
                        if (k < j && cards[k].checkBingo(sequence)) {
                            valid = false;
                            break;
                        }
                    }
                    if (!valid) {
                        break;
                    }
                }
                if (valid) {
                    min = Math.min(min, sequence.length);
                }
            }
            System.out.println(min == Integer.MAX_VALUE ? 0 : min);
        }
    }
}
