#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define EPS 1e-7
#define MAXN 15

typedef struct {
    int n;
    double x[MAXN], y[MAXN];
} Polygon;

double dist(Polygon *p, int i, int j) {
    return sqrt(pow(p->x[i] - p->x[j], 2) + pow(p->y[i] - p->y[j], 2));
}

double min_width(Polygon *p1, Polygon *p2, double L) {
    double min = 0, max = 1000000;
    while (max - min > EPS) {
        double mid = (min + max) / 2;
        int ok = 1;
        for (int i = 0; i < p1->n; i++) {
            for (int j = 0; j < p2->n; j++) {
                if (dist(p1, i, 0) + dist(p2, j, 0) < L) {
                    ok = 0;
                    break;
                }
            }
            if (!ok) break;
        }
        if (ok) max = mid;
        else min = mid;
    }
    return max;
}

int main() {
    int n;
    double L;
    Polygon p1, p2;

    while (scanf("%lf", &L) == 1) {
        if (L == 0) break;
        scanf("%d", &p1.n);
        for (int i = 0; i < p1.n; i++) {
            scanf("%lf%lf", &p1.x[i], &p1.y[i]);
        }
        scanf("%d", &p2.n);
        for (int i = 0; i < p2.n; i++) {
            scanf("%lf%lf", &p2.x[i], &p2.y[i]);
        }
        double min = 0, max = 1000000;
        while (max - min > EPS) {
            double mid = (min + max) / 2;
            int ok = 1;
            for (int i = 0; i < p1.n; i++) {
                for (int j = 0; j < p2.n; j++) {
                    if (dist(&p1, i, 0) + dist(&p2, j, 0) < L) {
                        ok = 0;
                        break;
                    }
                }
                if (!ok) break;
            }
            if (ok) max = mid;
            else min = mid;
        }
        printf("%.10lf\n", min_width(&p1, &p2, L));
    }
    return 0;
}
