#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>

using namespace std;

struct Point {
    double x, y;
};

double distance(const Point& p1, const Point& p2) {
    return sqrt(pow(p1.x - p2.x, 2) + pow(p1.y - p2.y, 2));
}

double minDistance(const vector<Point>& p1, const vector<Point>& p2) {
    double minDist = 1e9;
    for (const auto& p : p1) {
        for (const auto& q : p2) {
            minDist = min(minDist, distance(p, q));
        }
    }
    return minDist;
}

double minWidth(const vector<Point>& p1, const vector<Point>& p2, double L) {
    double minDist = minDistance(p1, p2);
    if (minDist > L) {
        return max(p1.back().x, p2.back().x) - min(p1.front().x, p2.front().x);
    } else {
        double minW = 1e9;
        for (const auto& p : p1) {
            double x = p.x;
            double minDist2 = 1e9;
            for (const auto& q : p2) {
                minDist2 = min(minDist2, distance(p, q));
            }
            if (minDist2 > L) {
                minW = min(minW, max(x, p2.back().x) - min(x, p2.front().x));
            }
        }
        for (const auto& p : p2) {
            double x = p.x;
            double minDist2 = 1e9;
            for (const auto& q : p1) {
                minDist2 = min(minDist2, distance(p, q));
            }
            if (minDist2 > L) {
                minW = min(minW, max(x, p1.back().x) - min(x, p1.front().x));
            }
        }
        return minW;
    }
}

int main() {
    double L;
    while (cin >> L) {
        if (L == 0) break;
        int n1, n2;
        cin >> n1 >> n2;
        vector<Point> p1(n1), p2(n2);
        for (auto& p : p1) {
            cin >> p.x >> p.y;
        }
        for (auto& p : p2) {
            cin >> p.x >> p.y;
        }
        double minW = 1e9;
        for (int i = 0; i < n1; ++i) {
            p1[i].x += L;
            minW = min(minW, minWidth(p1, p2, L));
            p1[i].x -= L;
        }
        for (int i = 0; i < n2; ++i) {
            p2[i].x += L;
            minW = min(minW, minWidth(p1, p2, L));
            p2[i].x -= L;
        }
        cout << fixed << setprecision(10) << minW << endl;
    }
    return 0;
}
