#include <stdio.h>
#include <math.h>

#define MAX_STARS 500
#define MAX_TELESCOPES 50

int main() {
    int n, m, i, j, count;
    double sx[MAX_STARS], sy[MAX_STARS], sz[MAX_STARS];
    double tx[MAX_TELESCOPES], ty[MAX_TELESCOPES], tz[MAX_TELESCOPES], phi[MAX_TELESCOPES];

    while (scanf("%d", &n) == 1) {
        if (n == 0) break;

        for (i = 0; i < n; i++) {
            scanf("%lf %lf %lf", &sx[i], &sy[i], &sz[i]);
        }

        scanf("%d", &m);

        for (i = 0; i < m; i++) {
            scanf("%lf %lf %lf %lf", &tx[i], &ty[i], &tz[i], &phi[i]);
        }

        count = 0;

        for (i = 0; i < n; i++) {
            for (j = 0; j < m; j++) {
                double dx = tx[j] - sx[i];
                double dy = ty[j] - sy[i];
                double dz = tz[j] - sz[i];
                double dist = sqrt(dx * dx + dy * dy + dz * dz);
                double theta = acos(dx / dist);
                double phi_j = phi[j];

                if (theta < phi_j) {
                    count++;
                    break;
                }
            }
        }

        printf("%d\n", count);
    }

    return 0;
}
