#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>

struct Star {
    double x, y, z;
};

struct Telescope {
    double x, y, z, phi;
};

bool compareStars(const Star& a, const Star& b) {
    return (a.x * a.x + a.y * a.y + a.z * a.z) < (b.x * b.x + b.y * b.y + b.z * b.z);
}

bool compareTelescopes(const Telescope& a, const Telescope& b) {
    return (a.x * a.x + a.y * a.y + a.z * a.z) < (b.x * b.x + b.y * b.y + b.z * b.z);
}

int main() {
    int n, m;
    while (std::cin >> n) {
        if (n == 0) break;

        std::vector<Star> stars(n);
        for (int i = 0; i < n; i++) {
            std::cin >> stars[i].x >> stars[i].y >> stars[i].z;
        }

        std::cin >> m;
        std::vector<Telescope> telescopes(m);
        for (int i = 0; i < m; i++) {
            std::cin >> telescopes[i].x >> telescopes[i].y >> telescopes[i].z >> telescopes[i].phi;
        }

        std::sort(stars.begin(), stars.end(), compareStars);
        std::sort(telescopes.begin(), telescopes.end(), compareTelescopes);

        int count = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                double theta = acos((stars[i].x * telescopes[j].x + stars[i].y * telescopes[j].y + stars[i].z * telescopes[j].z) /
                                     (sqrt(stars[i].x * stars[i].x + stars[i].y * stars[i].y + stars[i].z * stars[i].z) *
                                      sqrt(telescopes[j].x * telescopes[j].x + telescopes[j].y * telescopes[j].y + telescopes[j].z * telescopes[j].z)));
                if (theta < telescopes[j].phi) {
                    count++;
                    break;
                }
            }
        }

        std::cout << count << std::endl;
    }

    return 0;
}
