import math

def distance(p1, p2):
    return math.sqrt((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2 + (p1[2] - p2[2])**2)

def angle(p1, p2):
    return math.acos((p1[0]*p2[0] + p1[1]*p2[1] + p1[2]*p2[2]) / (distance(p1, (0, 0, 0)) * distance(p2, (0, 0, 0))))

def observable_stars(stars, telescopes):
    seen = set()
    for i, star in enumerate(stars):
        for j, telescope in enumerate(telescopes):
            if angle(star, telescope[:3]) < telescope[3]:
                seen.add(i)
    return len(seen)

def main():
    while True:
        n = int(input())
        stars = []
        for _ in range(n):
            stars.append(tuple(map(float, input().split())))
        m = int(input())
        telescopes = []
        for _ in range(m):
            telescopes.append(tuple(map(float, input().split())))
        if n == 0 and m == 0:
            break
        print(observable_stars(stars, telescopes))

if __name__ == "__main__":
    main()
