#include <stdio.h>
#include <stdlib.h>

typedef struct Point {
    int x, y;
} Point;

int orientation(Point p, Point q, Point r) {
    int val = (q.y - p.y) * (r.x - q.x) - (q.x - p.x) * (r.y - q.y);
    if (val == 0) return 0;
    return (val > 0) ? 1 : -1;
}

int isStarShape(Point points[], int n) {
    int l = 0, r = n - 1;
    while (l < r) {
        int m = l + (r - l) / 2;
        int o = orientation(points[l], points[m], points[r]);
        if (o == 0) return 0;
        if (o == -1) l = m + 1;
        else r = m;
    }
    return 1;
}

int main() {
    int n;
    while (scanf("%d", &n) == 1) {
        if (n == 0) break;
        Point points[n];
        for (int i = 0; i < n; i++) {
            scanf("%d %d", &points[i].x, &points[i].y);
        }
        printf("%d\n", isStarShape(points, n));
    }
    return 0;
}
