#include <iostream>
#include <vector>
#include <cmath>

using namespace std;

struct Point {
    int x, y;
};

bool isStarShape(vector<Point> points) {
    int n = points.size();
    for (int i = 0; i < n; i++) {
        int x = points[i].x, y = points[i].y;
        int x1 = points[(i + 1) % n].x, y1 = points[(i + 1) % n].y;
        for (int j = 0; j < n; j++) {
            if (j == i) continue;
            int x2 = points[j].x, y2 = points[j].y;
            if (x1 - x == 0) {
                if (y1 < y && y2 < y) continue;
                else return false;
            } else if (y1 - y == 0) {
                if (x1 < x && x2 < x) continue;
                else return false;
            } else {
                double k = (double)(x1 - x) / (y1 - y);
                double b = y - k * x;
                if (k * x2 + b < y2) continue;
                else return false;
            }
        }
        return true;
    }
    return false;
}

int main() {
    int n;
    while (cin >> n) {
        if (n == 0) break;
        vector<Point> points(n);
        for (int i = 0; i < n; i++) {
            cin >> points[i].x >> points[i].y;
        }
        cout << (isStarShape(points) ? "1" : "0") << endl;
    }
    return 0;
}
