import java.util.Scanner;

public class Main {
    static int n;
    static int[][] points;

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            n = scanner.nextInt();
            if (n == 0) break;
            points = new int[n][2];
            for (int i = 0; i < n; i++) {
                points[i][0] = scanner.nextInt();
                points[i][1] = scanner.nextInt();
            }
            System.out.println(isStarShape() ? 1 : 0);
        }
    }

    public static boolean isStarShape() {
        int[] x = new int[n];
        int[] y = new int[n];
        for (int i = 0; i < n; i++) {
            x[i] = points[i][0];
            y[i] = points[i][1];
        }
        for (int i = 0; i < n; i++) {
            int[] temp = x;
            x = y;
            y = temp;
        }
        int[] min = new int[n];
        int[] max = new int[n];
        for (int i = 0; i < n; i++) {
            min[i] = x[i];
            max[i] = x[i];
        }
        for (int i = 0; i < n; i++) {
            min[i] = Math.min(min[i], y[i]);
            max[i] = Math.max(max[i], y[i]);
        }
        int minY = min[0];
        int maxY = max[0];
        for (int i = 1; i < n; i++) {
            minY = Math.min(minY, min[i]);
            maxY = Math.max(maxY, max[i]);
        }
        for (int i = 0; i < n; i++) {
            if (y[i] < minY || y[i] > maxY) return false;
        }
        return true;
    }
}
