import sys

def orientation(p, q, r):
    val = (q[1] - p[1]) * (r[0] - q[0]) - (q[0] - p[0]) * (r[1] - q[1])
    if val == 0:
        return 0
    return 1 if val > 0 else -1

def is_star_shaped(n, points):
    for i in range(n):
        p = points[i]
        q = points[(i + 1) % n]
        for j in range(n):
            r = points[j]
            if (orientation(p, q, r) == 0 or
                orientation(p, q, r) != orientation(p, points[(j + 1) % n], r)):
                return False
    return True

def main():
    while True:
        n = int(sys.stdin.readline().strip())
        if n == 0:
            break
        points = []
        for _ in range(n):
            x, y = map(int, sys.stdin.readline().strip().split())
            points.append((x, y))
        print(1 if is_star_shaped(n, points) else 0)

if __name__ == "__main__":
    main()
