#include <iostream>
#include <queue>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

const int dx[] = {0, 0, 1, -1};
const int dy[] = {1, -1, 0, 0};

struct State {
    int x, y;
    vector<string> board;
    int steps;
};

bool operator<(const State& a, const State& b) {
    return a.steps > b.steps;
}

int bfs(const vector<string>& goal, const vector<string>& board) {
    queue<State> q;
    q.push({board[0].find('E') / 3, board[0].find('E') % 3, board, 0});
    vector<vector<bool>> visited(3, vector<bool>(3, false));
    visited[board[0].find('E') / 3][board[0].find('E') % 3] = true;

    while (!q.empty()) {
        State s = q.front();
        q.pop();

        if (s.board == goal) {
            return s.steps;
        }

        for (int i = 0; i < 4; ++i) {
            int nx = s.x + dx[i];
            int ny = s.y + dy[i];

            if (nx >= 0 && nx < 3 && ny >= 0 && ny < 3 && !visited[nx][ny]) {
                vector<string> newBoard = s.board;
                swap(newBoard[nx * 3 + ny], newBoard[s.x * 3 + s.y]);
                q.push({nx, ny, newBoard, s.steps + 1});
                visited[nx][ny] = true;
            }
        }
    }

    return -1;
}

int main() {
    while (true) {
        int x, y;
        cin >> x >> y;

        if (x == 0 && y == 0) {
            break;
        }

        vector<string> goal(3);
        for (int i = 0; i < 3; ++i) {
            cin >> goal[i];
        }

        vector<string> board(3);
        for (int i = 0; i < 3; ++i) {
            cin >> board[i];
        }

        cout << bfs(goal, board) << endl;
    }

    return 0;
}
