from collections import deque

def is_valid(x, y):
    return 1 <= x <= 3 and 1 <= y <= 3

def get_neighbors(x, y):
    neighbors = []
    if is_valid(x - 1, y):
        neighbors.append((x - 1, y))
    if is_valid(x + 1, y):
        neighbors.append((x + 1, y))
    if is_valid(x, y - 1):
        neighbors.append((x, y - 1))
    if is_valid(x, y + 1):
        neighbors.append((x, y + 1))
    return neighbors

def get_cube_color(board, x, y):
    for i in range(3):
        for j in range(3):
            if board[i][j] == 'E' and (i, j) != (x, y):
                return board[i][j]
    return None

def solve(board, target, empty_x, empty_y, steps):
    queue = deque([(empty_x, empty_y, 0)])
    visited = set([(empty_x, empty_y, 0)])
    while queue:
        x, y, step = queue.popleft()
        if step > 30:
            return -1
        if board[x][y] == target[x][y]:
            return step
        for nx, ny in get_neighbors(x, y):
            cube_color = get_cube_color(board, nx, ny)
            if cube_color is not None and (nx, ny, step + 1) not in visited:
                queue.append((nx, ny, step + 1))
                visited.add((nx, ny, step + 1))
    return -1

def main():
    while True:
        x, y = map(int, input().split())
        board = []
        for _ in range(3):
            board.append(list(input().strip()))
        target = []
        for _ in range(3):
            target.append(list(input().strip()))
        print(solve(board, target, x, y, 0))
        if x == 0 and y == 0:
            break

if __name__ == "__main__":
    main()
