#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MAX_N 1120
#define MAX_K 14

int is_prime(int n) {
    if (n <= 1) return 0;
    if (n <= 3) return 1;
    if (n % 2 == 0 || n % 3 == 0) return 0;
    for (int i = 5; i * i <= n; i += 6)
        if (n % i == 0 || n % (i + 2) == 0)
            return 0;
    return 1;
}

int count_ways(int n, int k) {
    int primes[MAX_N + 1];
    int num_primes = 0;
    for (int i = 2; i <= n; i++) {
        if (is_prime(i)) {
            primes[num_primes++] = i;
        }
    }

    int dp[k + 1][n + 1];
    for (int i = 0; i <= k; i++) {
        for (int j = 0; j <= n; j++) {
            dp[i][j] = 0;
        }
    }

    for (int i = 1; i <= num_primes; i++) {
        for (int j = 1; j <= k; j++) {
            for (int p = 0; p < i; p++) {
                if (primes[p] <= n) {
                    dp[j][n - primes[p]] += dp[j - 1][n - primes[p] - primes[i - 1 - p]];
                }
            }
        }
    }

    return dp[k][n];
}

int main() {
    int n, k;
    while (scanf("%d %d", &n, &k) == 2) {
        if (n == 0 && k == 0) break;
        printf("%d\n", count_ways(n, k));
    }
    return 0;
}
