def sieve_of_eratosthenes(n):
    primes = [True] * (n + 1)
    primes[0] = primes[1] = False
    p = 2
    while p**2 <= n:
        if primes[p]:
            for i in range(p**2, n + 1, p):
                primes[i] = False
        p += 1
    return [p for p in range(2, n + 1) if primes[p]]

def count_ways(n, k, primes):
    ways = 0
    def backtrack(i, current_sum, current_set):
        nonlocal ways
        if len(current_set) == k:
            if current_sum == n:
                ways += 1
            return
        for j in range(i, len(primes)):
            if current_sum + primes[j] <= n:
                backtrack(j + 1, current_sum + primes[j], current_set + [primes[j]])
    backtrack(0, 0, [])
    return ways

primes = sieve_of_eratosthenes(1120)
while True:
    try:
        n, k = map(int, input().split())
        print(count_ways(n, k, primes))
    except ValueError:
        break
