#include <iostream>
#include <string>

int main() {
    int n;
    std::cin >> n;

    for (int i = 0; i < n; i++) {
        std::string text;
        std::cin >> text;
        size_t pos = text.find("Hoshino");
        while (pos != std::string::npos) {
            text.replace(pos, 7, "Hoshina");
            pos = text.find("Hoshino");
        }
        std::cout << text << std::endl;
    }

    return 0;
}
