import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.*;

public class Main {
    static class Pair {
        int x, y;

        public Pair(int x, int y) {
            this.x = x;
            this.y = y;
        }
    }

    static int[][] dirs = {{0, 1}, {0, -1}, {1, 0}, {-1, 0}};

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String line;
        while ((line = br.readLine()) != null) {
            if (line.equals("2 3")) break;
            String[] parts = line.split(" ");
            int n = Integer.parseInt(parts[0]);
            int m = Integer.parseInt(parts[1]);
            int[][] grid = new int[n][m];
            for (int i = 0; i < n; i++) {
                parts = br.readLine().split(" ");
                for (int j = 0; j < m; j++) {
                    grid[i][j] = Integer.parseInt(parts[j]);
                }
            }
            int[][] dist = new int[n][m];
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < m; j++) {
                    dist[i][j] = Integer.MAX_VALUE;
                }
            }
            Queue<Pair> q = new LinkedList<>();
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < m; j++) {
                    if (grid[i][j] == 2) {
                        dist[i][j] = 0;
                        q.add(new Pair(i, j));
                    }
                }
            }
            while (!q.isEmpty()) {
                Pair p = q.poll();
                for (int[] dir : dirs) {
                    int x = p.x + dir[0];
                    int y = p.y + dir[1];
                    if (x >= 0 && x < n && y >= 0 && y < m && grid[x][y] != 1 && dist[x][y] == Integer.MAX_VALUE) {
                        dist[x][y] = dist[p.x][p.y] + 1;
                        q.add(new Pair(x, y));
                    }
                }
            }
            int min1 = Integer.MAX_VALUE;
            int min2 = Integer.MAX_VALUE;
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < m; j++) {
                    if (grid[i][j] == 3) {
                        min1 = Math.min(min1, dist[i][j]);
                        min2 = Math.min(min2, dist[i][j]);
                    }
                }
            }
            if (min1 == Integer.MAX_VALUE || min2 == Integer.MAX_VALUE) {
                System.out.println(0);
            } else {
                System.out.println(min1 + min2);
            }
        }
    }
}
