from collections import deque

def bfs(grid, start, end):
    rows, cols = len(grid), len(grid[0])
    visited = [[False]*cols for _ in range(rows)]
    queue = deque([(start, 0)])
    visited[start[0]][start[1]] = True
    while queue:
        (x, y), dist = queue.popleft()
        if (x, y) == end:
            return dist
        for dx, dy in [(1, 0), (-1, 0), (0, 1), (0, -1)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < rows and 0 <= ny < cols and not visited[nx][ny] and grid[nx][ny] != 1:
                queue.append(((nx, ny), dist + 1))
                visited[nx][ny] = True
    return -1

def manhattan_wiring(grid):
    rows, cols = len(grid), len(grid[0])
    twos = []
    threes = []
    for i in range(rows):
        for j in range(cols):
            if grid[i][j] == 2:
                twos.append((i, j))
            elif grid[i][j] == 3:
                threes.append((i, j))
    if len(twos) < 2 or len(threes) < 2:
        return 0
    twos.sort(key=lambda x: (x[0], x[1]))
    threes.sort(key=lambda x: (x[0], x[1]))
    twos_dist = bfs(grid, twos[0], twos[1])
    threes_dist = bfs(grid, threes[0], threes[1])
    if twos_dist == -1 or threes_dist == -1:
        return 0
    return twos_dist + threes_dist

def main():
    while True:
        try:
            rows, cols = map(int, input().split())
            grid = []
            for _ in range(rows):
                grid.append(list(map(int, input().split())))
            print(manhattan_wiring(grid))
        except ValueError:
            break

if __name__ == "__main__":
    main()
