#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_N 7
#define MAX_R 300

int max_area(int n, int r[], int x, int y, int z) {
    int i, j, k, area;
    int dp[MAX_N][MAX_N][MAX_N];

    for (i = 0; i < n; i++) {
        for (j = 0; j < n; j++) {
            for (k = 0; k < n; k++) {
                dp[i][j][k] = -1;
            }
        }
    }

    dp[0][0][0] = 0;

    for (i = 0; i < n; i++) {
        for (j = 0; j < n; j++) {
            for (k = 0; k < n; k++) {
                if (dp[i][j][k] == -1) continue;

                for (int l = 0; l < n; l++) {
                    if (i + l >= n) break;
                    if (j + l >= n) break;
                    if (k + l >= n) break;

                    int new_x = x + r[i + l];
                    int new_y = y + r[j + l];
                    int new_z = z + r[k + l];

                    if (new_x <= new_y && new_y <= new_z) {
                        area = dp[i][j][k] + new_x * new_y;
                        if (area > dp[i + l][j + l][k + l]) {
                            dp[i + l][j + l][k + l] = area;
                        }
                    }
                }
            }
        }
    }

    int max = -1;
    for (i = 0; i < n; i++) {
        for (j = 0; j < n; j++) {
            for (k = 0; k < n; k++) {
                if (dp[i][j][k] > max) {
                    max = dp[i][j][k];
                }
            }
        }
    }

    return max;
}

int main() {
    int n, i, j, k, max;
    int r[MAX_N];

    while (scanf("%d", &n) == 1) {
        if (n == 0) break;

        for (i = 0; i < n; i++) {
            scanf("%d", &r[i]);
        }

        max = 0;
        for (i = 0; i < n; i++) {
            for (j = i; j < n; j++) {
                for (k = j; k < n; k++) {
                    int area = max_area(n, r, r[i], r[j], r[k]);
                    if (area > max) {
                        max = area;
                    }
                }
            }
        }

        if (max == 0) {
            max = -1;
        }

        printf("%d\n", max);

        for (i = 0; i < n; i++) {
            r[i] = 0;
        }
    }

    return 0;
}
