using namespace std;

long long maxArea(vector<int>& rods, int n) {
    if (n == 3) {
        int a = rods[0], b = rods[1], c = rods[2];
        if (a + b <= c || a + c <= b || b + c <= a) return -1;
        return (long long)a * b;
    } else if (n == 4) {
        int a = rods[0], b = rods[1], c = rods[2], d = rods[3];
        if (a + b <= c || a + c <= b || b + c <= a || a + d <= c || a + c <= d || b + d <= c || c + d <= a || b + c <= d || a + b <= d) return -1;
        return (long long)a * b + (long long)c * d;
    } else if (n == 5) {
        int a = rods[0], b = rods[1], c = rods[2], d = rods[3], e = rods[4];
        if (a + b <= c || a + c <= b || b + c <= a || a + d <= c || a + c <= d || b + d <= c || c + d <= a || a + e <= c || a + c <= e || b + e <= c || c + e <= a || b + c <= e || d + e <= a || d + a <= e || e + a <= d || e + d <= b || b + e <= d || d + b <= e || c + e <= d || d + c <= e || e + c <= d || b + d <= e || e + b <= d || a + b <= e || a + e <= b || b + a <= e || e + a <= b) return -1;
        return (long long)a * b + (long long)c * d + (long long)e * (a + b + c + d);
    } else if (n == 6) {
        int a = rods[0], b = rods[1], c = rods[2], d = rods[3], e = rods[4], f = rods[5];
        if (a + b <= c || a + c <= b || b + c <= a || a + d <= c || a + c <= d || b + d <= c || c + d <= a || a + e <= c || a + c <= e || b + e <= c || c + e <= a || b + c <= e || d + e <= a || d + a <= e || e + a <= d || e + d <= b || b + e <= d || d + b <= e || c + e <= d || d + c <= e || e + c <= d || b + d <= e || e + b <= d || a + b <= e || a + e <= b || b + a <= e || e + a <= b || a + f <= c || a + c <= f || b + f <= c || c + f <= a || b + c <= f || d + f <= a || d + a <= f || f + a <= d || f + d <= b || b + f <= d || d + b <= f || c + f <= d || d + c <= f || f + c <= d || b + d <= f || f + b <= d || a + b <= f || a + f <= b || b + a <= f || f + a <= b) return -1;
        return (long long)a * b + (long long)c * d + (long long)e * (a + b + c + d) + (long long)f * (a + b + c + d + e);
    }
    return -1;
}

