import java.util.Arrays;
import java.util.Scanner;

public class Main {
    static int max(int a, int b) {
        return Math.max(a, b);
    }

    static int min(int a, int b) {
        return Math.min(a, b);
    }

    static int maxArea(int[] a, int n, int[] dp, int[] sum) {
        if (n == 0) return 0;
        if (dp[n] != -1) return dp[n];
        int max = 0;
        for (int i = 0; i < n; i++) {
            int[] b = new int[n - 1];
            System.arraycopy(a, 0, b, 0, i);
            System.arraycopy(a, i + 1, b, i, n - i - 1);
            int area = maxArea(b, n - 1, dp, sum) + sum[n - 1] - sum[i];
            max = max(max, area);
        }
        dp[n] = max;
        return max;
    }

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int n = scanner.nextInt();
            if (n == 0) break;
            int[] a = new int[n];
            for (int i = 0; i < n; i++) {
                a[i] = scanner.nextInt();
            }
            int[] dp = new int[n + 1];
            Arrays.fill(dp, -1);
            int[] sum = new int[n + 1];
            for (int i = 1; i <= n; i++) {
                sum[i] = sum[i - 1] + a[i - 1];
            }
            int max = maxArea(a, n, dp, sum);
            if (max == 0) System.out.println(-1);
            else System.out.println(max);
        }
    }
}
