#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <ctype.h>

#define MAX_RULES 100
#define MAX_NAME 100
#define MAX_RULE 20

typedef struct {
    char letter;
    char rule[MAX_RULE];
} Rule;

void dfs(char *name, int length, Rule rules[], int n, int *found) {
    if (length == 0) {
        if (strcmp(name, "S") == 0) return;
        *found = 1;
        return;
    }

    for (int i = 0; i < n; i++) {
        Rule rule = rules[i];
        if (rule.letter == name[length - 1]) {
            char new_name[MAX_NAME];
            strcpy(new_name, name);
            for (int j = 0; j < length - 1; j++) {
                if (new_name[j] == rule.letter) {
                    for (int k = 0; k < strlen(rule.rule); k++) {
                        new_name[j] = rule.rule[k];
                    }
                    break;
                }
            }
            dfs(new_name, length - 1, rules, n, found);
        }
    }
}

int main() {
    int n, l;
    Rule rules[MAX_RULES];
    int found = 0;
    char name[MAX_NAME];

    while (scanf("%d %d", &n, &l) == 2) {
        if (n == 0 && l == 0) break;

        for (int i = 0; i < n; i++) {
            scanf(" %c = %s", &rules[i].letter, rules[i].rule);
        }

        for (int i = 0; i < l; i++) {
            name[i] = 'a';
        }
        name[l] = '\0';

        dfs(name, l, rules, n, &found);

        if (found) {
            qsort(name, l, 1, (int (*)(const void *, const void *))strcmp);
            printf("%s\n", name);
        } else {
            printf("-\n");
        }

        found = 0;
    }

    return 0;
}
