#include <iostream>
#include <queue>
#include <string>
#include <unordered_map>
#include <unordered_set>

using namespace std;

struct State {
    string name;
    int length;
    bool visited;
};

bool operator<(const State& a, const State& b) {
    return a.name < b.name;
}

int main() {
    while (true) {
        int n, l;
        cin >> n >> l;
        if (n == 0 && l == 0) break;

        unordered_map<char, string> rules;
        for (int i = 0; i < n; i++) {
            char c;
            string rule;
            cin >> c >> rule;
            rules[c] = rule;
        }

        priority_queue<State> queue;
        queue.push({{"S"}, 1, false});

        unordered_set<string> visited;

        while (!queue.empty()) {
            State state = queue.top();
            queue.pop();

            if (state.length == l) {
                cout << state.name << endl;
                break;
            }

            if (visited.find(state.name) != visited.end()) continue;

            visited.insert(state.name);

            for (auto& rule : rules) {
                string new_name = state.name;
                size_t pos = new_name.find(rule.first);
                while (pos != string::npos) {
                    new_name.replace(pos, 1, rule.second);
                    pos = new_name.find(rule.first, pos + 1);
                }

                if (new_name.length() > state.length) continue;

                queue.push({new_name, state.length + new_name.length() - state.name.length(), false});
            }
        }

        if (queue.empty()) cout << "-" << endl;
    }

    return 0;
}
