from collections import deque

def dfs(node, visited, graph, length, rules):
    if length == 0:
        return [node]
    visited.add(node)
    result = []
    for neighbor in graph[node]:
        if neighbor not in visited:
            for path in dfs(neighbor, visited, graph, length - 1, rules):
                result.append(node + path)
    visited.remove(node)
    return result

def find_name(n, l, rules):
    graph = {chr(i): [] for i in range(ord('A'), ord('Z')+1)}
    for rule in rules:
        left, right = rule.split('=')
        graph[ord(left[0])].append(right)
    visited = set()
    result = set()
    for node in graph:
        for path in dfs(node, visited, graph, l, rules):
            if all(c.islower() for c in path):
                result.add(path)
    return min(result, key=lambda x: x) if result else '-'

def main():
    while True:
        n, l = map(int, input().split())
        if n == 0 and l == 0:
            break
        rules = [input().strip() for _ in range(n)]
        print(find_name(n, l, rules))

if __name__ == "__main__":
    main()
