#include <iostream>
#include <queue>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

struct Node {
    int dist;
    int path;
    int node;
    bool operator<(const Node& other) const {
        if (dist == other.dist) {
            return path < other.path;
        }
        return dist < other.dist;
    }
};

int main() {
    int n, m, k, a, b;
    while (cin >> n >> m >> k >> a >> b) {
        if (n == 0 && m == 0 && k == 0 && a == 0 && b == 0) {
            break;
        }

        vector<vector<pair<int, int>>> graph(n + 1);
        for (int i = 0; i < m; i++) {
            int x, y, d;
            cin >> x >> y >> d;
            graph[x].push_back({y, d});
        }

        vector<int> dist(n + 1, 1e9);
        vector<int> path(n + 1, 0);
        priority_queue<Node> pq;
        pq.push({0, 0, a});

        while (!pq.empty()) {
            Node node = pq.top();
            pq.pop();
            if (node.dist > dist[node.node]) {
                continue;
            }
            for (auto& edge : graph[node.node]) {
                int newDist = node.dist + edge.second;
                if (newDist < dist[edge.first]) {
                    dist[edge.first] = newDist;
                    path[edge.first] = node.path * 100 + edge.first;
                    pq.push({newDist, path[edge.first], edge.first});
                }
            }
        }

        if (dist[b] == 1e9) {
            cout << "None" << endl;
        } else {
            vector<int> res;
            int node = b;
            while (node != a) {
                res.push_back(node);
                node = path[node] % 100;
            }
            res.push_back(a);
            reverse(res.begin(), res.end());
            for (int i = 0; i < res.size() - 1; i++) {
                cout << res[i] << "-";
            }
            cout << res.back() << endl;
        }
    }

    return 0;
}
