#include <stdio.h>
#include <stdbool.h>
#include <math.h>

#define MAX 1299709

bool is_prime(int n) {
    if (n <= 1) return false;
    if (n <= 3) return true;
    if (n % 2 == 0 || n % 3 == 0) return false;
    for (int i = 5; i * i <= n; i += 6)
        if (n % i == 0 || n % (i + 2) == 0)
            return false;
    return true;
}

int main() {
    int primes[MAX], count = 0;
    primes[0] = 2;
    for (int i = 3; i <= MAX; i += 2) {
        if (is_prime(i)) {
            primes[count++] = i;
        }
    }

    int n;
    while (scanf("%d", &n) == 1) {
        if (n == 0) break;
        int low = 0, high = count - 1;
        while (low <= high) {
            int mid = (low + high) / 2;
            if (primes[mid] - n <= primes[mid - 1]) {
                low = mid + 1;
            } else if (primes[mid] - n >= primes[mid + 1]) {
                high = mid - 1;
            } else {
                int gap = primes[mid] - n;
                if (is_prime(n)) {
                    printf("0\n");
                } else {
                    int i;
                    for (i = 0; i < count && primes[i] <= primes[mid] + gap; i++) {
                        if (!is_prime(primes[i])) break;
                    }
                    printf("%d\n", primes[i] - primes[mid] - 1);
                }
                break;
            }
        }
    }

    return 0;
}
