#include <iostream>
#include <vector>
#include <cmath>

bool isPrime(int n) {
    if (n <= 1) return false;
    if (n == 2) return true;
    if (n % 2 == 0) return false;
    int sqrtN = std::sqrt(n);
    for (int i = 3; i <= sqrtN; i += 2) {
        if (n % i == 0) return false;
    }
    return true;
}

int primeGap(int n) {
    std::vector<int> primes;
    int prime = 2;
    while (primes.size() < 100000) {
        if (isPrime(prime)) primes.push_back(prime);
        prime++;
    }
    int index = 0;
    while (index < primes.size() - 1) {
        int gap = primes[index + 1] - primes[index];
        if (primes[index] <= n && n <= primes[index + 1] - 1 && !isPrime(n)) {
            return gap;
        }
        index++;
    }
    return 0;
}

int main() {
    int n;
    while (std::cin >> n) {
        if (n == 0) break;
        std::cout << primeGap(n) << std::endl;
    }
    return 0;
}
