import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        boolean[] isPrime = new boolean[1299709];
        Arrays.fill(isPrime, true);
        isPrime[0] = isPrime[1] = false;
        for (int i = 2; i * i <= 1299709; i++) {
            if (isPrime[i]) {
                for (int j = i * i; j <= 1299709; j += i) {
                    isPrime[j] = false;
                }
            }
        }
        int[] primes = new int[1299709];
        int count = 0;
        for (int i = 2; i <= 1299709; i++) {
            if (isPrime[i]) {
                primes[count++] = i;
            }
        }
        while (true) {
            int n = scanner.nextInt();
            if (n == 0) {
                break;
            }
            int left = 0, right = 1;
            while (primes[right] < n) {
                right++;
            }
            if (isPrime[n]) {
                System.out.println(0);
            } else {
                while (primes[right] - primes[left] > 1) {
                    if (!isPrime[primes[right] - 1]) {
                        System.out.println(primes[right] - primes[left] - 1);
                        break;
                    }
                    left++;
                }
                if (primes[right] - primes[left] == 1) {
                    System.out.println(0);
                }
            }
        }
    }
}
