#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define NMAX 101
#define TMAX 101

int n, t, l, b;
int lose[NMAX], back[NMAX];
int dp[NMAX][TMAX];

void read_input() {
    scanf("%d %d %d %d", &n, &t, &l, &b);
    for (int i = 0; i < l; i++) {
        scanf("%d", &lose[i]);
    }
    for (int i = 0; i < b; i++) {
        scanf("%d", &back[i]);
    }
}

void solve() {
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= t; j++) {
            dp[i][j] = 0;
        }
    }
    dp[0][0] = 1;
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= t; j++) {
            if (i == n) {
                printf("%.6f\n", dp[i][j] / (double)pow(6, j));
                return;
            }
            if (i == 0) {
                if (j > 0) {
                    dp[i][j] = dp[i][j - 1] / 6.0;
                }
            } else {
                if (j > 0) {
                    dp[i][j] = (dp[i - 1][j - 1] + dp[i][j - 1]) / 6.0;
                }
                if (lose[i - 1] == i - 1) {
                    if (j > 1) {
                        dp[i][j] += dp[i][j - 2] / 6.0;
                    }
                }
                if (back[i - 1] == i - 1) {
                    if (j > 0) {
                        dp[i][j] += dp[0][j - 1] / 6.0;
                    }
                }
            }
        }
    }
}

int main() {
    while (1) {
        read_input();
        if (n == 0 && t == 0 && l == 0 && b == 0) {
            break;
        }
        solve();
    }
    return 0;
}
