#include <iostream>
#include <vector>
#include <cmath>

using namespace std;

double probability(int n, int t, int l, int b, vector<int> lose, vector<int> back) {
    double dp[t + 1][n + 1];
    for (int i = 0; i <= t; i++) {
        for (int j = 0; j <= n; j++) {
            dp[i][j] = 0;
        }
    }
    dp[0][0] = 1;

    for (int i = 1; i <= t; i++) {
        for (int j = 0; j <= n; j++) {
            if (j == 0) {
                dp[i][j] = dp[i - 1][j];
            } else if (j == n) {
                dp[i][j] = dp[i - 1][j - 1] * 1.0 / 6;
            } else {
                double p = 0;
                for (int k = 1; k <= 6; k++) {
                    int nj = j + k;
                    if (nj > n) {
                        nj -= (nj - n);
                    }
                    if (nj == 0) {
                        p += dp[i - 1][j];
                    } else if (nj == n) {
                        p += dp[i - 1][j - 1] * 1.0 / 6;
                    } else {
                        bool loseHere = false;
                        for (int x = 0; x < lose.size(); x++) {
                            if (nj == lose[x]) {
                                loseHere = true;
                                break;
                            }
                        }
                        bool backHere = false;
                        for (int x = 0; x < back.size(); x++) {
                            if (nj == back[x]) {
                                backHere = true;
                                break;
                            }
                        }
                        if (!loseHere && !backHere) {
                            p += dp[i - 1][j - 1] * 1.0 / 6;
                        }
                    }
                }
                dp[i][j] = p;
            }
        }
    }

    return dp[t][n];
}

int main() {
    int n, t, l, b;
    vector<int> lose, back;
    while (cin >> n >> t >> l >> b) {
        if (n == 0 && t == 0 && l == 0 && b == 0) {
            break;
        }
        for (int i = 0; i < l; i++) {
            int x;
            cin >> x;
            lose.push_back(x);
        }
        for (int i = 0; i < b; i++) {
            int x;
            cin >> x;
            back.push_back(x);
        }
        double p = probability(n, t, l, b, lose, back);
        cout << fixed << setprecision(7) << p << endl;
        lose.clear();
        back.clear();
    }
    return 0;
}
