import java.util.*;
import java.io.*;

public class Main {
    static final int MAXN = 105;
    static final double EPS = 1e-7;

    static double[][] dp = new double[MAXN][MAXN];
    static int N, T, L, B;
    static int[] Lose, Back;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);

        while (true) {
            StringTokenizer st = new StringTokenizer(br.readLine());
            if (st.nextToken().equals("0")) break;

            N = Integer.parseInt(st.nextToken());
            T = Integer.parseInt(st.nextToken());
            L = Integer.parseInt(st.nextToken());
            B = Integer.parseInt(st.nextToken());

            Lose = new int[L];
            Back = new int[B];

            st = new StringTokenizer(br.readLine());
            for (int i = 0; i < L; i++) Lose[i] = Integer.parseInt(st.nextToken());
            for (int i = 0; i < B; i++) Back[i] = Integer.parseInt(st.nextToken());

            for (int i = 0; i <= N; i++) {
                Arrays.fill(dp[i], 0);
            }

            for (int i = 1; i <= N; i++) {
                dp[i][0] = 1;
            }

            for (int i = 1; i <= N; i++) {
                for (int j = 1; j <= T; j++) {
                    if (i == N) {
                        dp[i][j] = 1;
                    } else {
                        double p = 1.0 / 6;
                        for (int k = 1; k <= 6; k++) {
                            int x = i + k;
                            if (x > N) x = N - (x - N);
                            if (j - 1 >= 0) {
                                dp[i][j] += p * (dp[x][j - 1] + (Lose.contains(x) ? 0 : dp[x][j - 1]));
                            } else {
                                dp[i][j] += p * (dp[x][j - 1] + (Lose.contains(x) ? 0 : dp[x][j - 1]));
                            }
                            if (Back.contains(x)) {
                                dp[i][j] += p * dp[0][j - 1];
                            }
                        }
                    }
                }
            }

            double ans = 0;
            for (int i = 1; i <= T; i++) {
                ans += dp[N][i];
            }

            pw.printf("%.6f\n", ans);
        }

        pw.close();
    }
}
