import sys
import random

def roll_dice():
    return random.randint(1, 6)

def play_game(N, T, L, B, lose, back):
    position = 0
    for _ in range(T):
        roll = roll_dice()
        position = (position + roll) % (N + 1)
        if position == 0:
            return 1
        if position == N:
            return 1
        if position in lose:
            return 0
        if position in back:
            position = 0
    return 0

def compute_probability(N, T, L, B, lose, back):
    total = 0
    for _ in range(1000000):
        total += play_game(N, T, L, B, lose, back)
    return total / 1000000

def main():
    for line in sys.stdin:
        line = line.strip().split()
        if line == ['0', '0', '0', '0']:
            break
        N, T, L, B = map(int, line)
        lose = list(map(int, input().split()))[:L]
        back = list(map(int, input().split()))[:B]
        print(compute_probability(N, T, L, B, lose, back))

if __name__ == "__main__":
    main()
