#include <stdio.h>
#include <stdlib.h>

#define MAX_SIZE 100

int main() {
    int n, i, j;
    int **table;
    int *row_sums, *col_sums;

    while (1) {
        scanf("%d", &n);
        if (n == 0) break;

        table = (int **)malloc(n * sizeof(int *));
        row_sums = (int *)malloc(n * sizeof(int));
        col_sums = (int *)malloc(n * sizeof(int));

        for (i = 0; i < n; i++) {
            table[i] = (int *)malloc(n * sizeof(int));
            for (j = 0; j < n; j++) {
                scanf("%d", &table[i][j]);
                row_sums[i] += table[i][j];
                col_sums[j] += table[i][j];
            }
        }

        for (i = 0; i < n; i++) {
            for (j = 0; j < n; j++) {
                printf("%5d", table[i][j]);
            }
            printf("%5d\n", row_sums[i]);
        }

        for (j = 0; j < n; j++) {
            printf("%5d", col_sums[j]);
        }
        printf("%5d\n", n * n);

        for (i = 0; i < n; i++) {
            free(table[i]);
        }
        free(table);
        free(row_sums);
        free(col_sums);
    }

    return 0;
}
