#include <stdio.h>
#include <math.h>

#define MAX 100

typedef struct {
    int x, y;
} Point;

int sign(double x) {
    if (x > 0) return 1;
    if (x < 0) return -1;
    return 0;
}

double cross_product(Point p1, Point p2, Point p3) {
    return (p2.x - p1.x) * (p3.y - p1.y) - (p2.y - p1.y) * (p3.x - p1.x);
}

int is_valid(Point p1, Point p2, Point p3, Point p4, Point p5, Point p6) {
    double c1 = cross_product(p1, p2, p3);
    double c2 = cross_product(p1, p2, p4);
    double c3 = cross_product(p1, p2, p5);
    double c4 = cross_product(p1, p2, p6);

    if (c1 * c2 < 0 || c1 * c3 < 0 || c1 * c4 < 0) return 0;

    c1 = cross_product(p2, p3, p4);
    c2 = cross_product(p2, p3, p5);
    c3 = cross_product(p2, p3, p6);

    if (c1 * c2 < 0 || c1 * c3 < 0) return 0;

    c1 = cross_product(p3, p4, p5);
    c2 = cross_product(p3, p4, p6);

    if (c1 * c2 < 0) return 0;

    c1 = cross_product(p4, p5, p6);

    if (c1 < 0) return 0;

    return 1;
}

