#include <iostream>
#include <cmath>
#include <algorithm>

using namespace std;

struct Point {
    int x, y;
};

double distance(Point a, Point b) {
    return sqrt(pow(a.x - b.x, 2) + pow(a.y - b.y, 2));
}

double calculateHeight(Point a, Point b, Point c, Point d, Point e, Point f) {
    double ab = distance(a, b);
    double bc = distance(b, c);
    double ac = distance(a, c);
    double de = distance(d, e);
    double ef = distance(e, f);
    double df = distance(d, f);

    if (ab + bc != ac || de + ef != df || ab + de != ac || bc + df != ef || ac + ef != de || ac + de != bc || ab + df != ef) {
        return -1;
    }

    double h = (ab * bc * ac) / (2 * sqrt(pow(ab, 2) * pow(bc, 2) - pow(ab * bc, 2)));
    return h;
}

int main() {
    int t;
    cin >> t;
    while (t--) {
        Point p[3];
        for (int i = 0; i < 3; i++) {
            cin >> p[i].x >> p[i].y;
        }

        double minH = 1e9;
        for (int i = 0; i < 3; i++) {
            for (int j = i + 1; j < 3; j++) {
                for (int k = j + 1; k < 3; k++) {
                    Point q[3];
                    q[0] = p[i];
                    q[1] = p[j];
                    q[2] = p[k];

                    for (int x = -100; x <= 100; x++) {
                        for (int y = -100; y <= 100; y++) {
                            q[0].x = x;
                            q[0].y = y;
                            q[1].x = x;
                            q[1].y = y;
                            q[2].x = x;
                            q[2].y = y;

                            double h = calculateHeight(p[0], p[1], p[2], q[0], q[1], q[2]);
                            if (h > 0 && h < minH) {
                                minH = h;
                            }
                        }
                    }
                }
            }
        }

        if (minH == 1e9) {
            cout << -1 << endl;
        } else {
            cout << fixed << setprecision(5) << minH << endl;
        }
    }

    return 0;
}
