import java.util.*;
import java.io.*;

public class Main {
    static class Point {
        int x, y;

        public Point(int x, int y) {
            this.x = x;
            this.y = y;
        }
    }

    static double dist(Point a, Point b) {
        return Math.sqrt(Math.pow(a.x - b.x, 2) + Math.pow(a.y - b.y, 2));
    }

    static double cross(Point a, Point b, Point c) {
        return (b.x - a.x) * (c.y - a.y) - (b.y - a.y) * (c.x - a.x);
    }

    static boolean check(Point[] p, Point[] q, Point[] r) {
        return Math.abs(cross(p[0], p[1], q[0])) > 1e-6 && Math.abs(cross(p[1], p[2], r[0])) > 1e-6
                && Math.abs(cross(q[1], q[2], r[0])) > 1e-6;
    }

    static double solve(Point[] p) {
        double ans = 1e9;
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                for (int k = 0; k < 3; k++) {
                    if (i != j && i != k && j != k) {
                        Point[] q = new Point[] { p[(i + 1) % 3], p[(i + 2) % 3], new Point(p[i].x, p[j].y) };
                        Point[] r = new Point[] { p[(j + 1) % 3], p[(j + 2) % 3], new Point(p[j].x, p[k].y) };
                        if (check(p, q, r)) {
                            double h = Math.abs(cross(p[0], p[1], q[0])) / dist(p[0], q[0]);
                            ans = Math.min(ans, h);
                        }
                    }
                }
            }
        }
        return ans == 1e9 ? -1 : ans;
    }

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        while (true) {
            Point[] p = new Point[3];
            for (int i = 0; i < 3; i++) {
                p[i] = new Point(sc.nextInt(), sc.nextInt());
            }
            if (p[0].x == 0 && p[0].y == 0 && p[1].x == 0 && p[1].y == 0 && p[2].x == 0 && p[2].y == 0) {
                break;
            }
            System.out.println(solve(p));
        }
    }
}
