#include <iostream>
#include <vector>
#include <queue>
#include <cmath>
#include <algorithm>

using namespace std;

struct Point {
    int x, y;
};

struct Edge {
    int x1, y1, x2, y2;
};

struct Intersection {
    int x, y;
};

struct Compare {
    bool operator()(const pair<int, int>& a, const pair<int, int>& b) {
        return a.first > b.first;
    }
};

int main() {
    int n;
    while (cin >> n) {
        if (n == 0) break;

        vector<Point> points(n + 2);
        vector<Edge> edges(n);
        vector<Intersection> intersections;

        cin >> points[0].x >> points[0].y;
        cin >> points[n + 1].x >> points[n + 1].y;

        for (int i = 0; i < n; i++) {
            cin >> edges[i].x1 >> edges[i].y1 >> edges[i].x2 >> edges[i].y2;
            points[i + 1].x = edges[i].x1;
            points[i + 1].y = edges[i].y1;
        }

        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                if (edges[i].x1 == edges[j].x1 && edges[i].y1 == edges[j].y1) {
                    intersections.push_back({edges[i].x1, edges[i].y1});
                }
                if (edges[i].x1 == edges[j].x2 && edges[i].y1 == edges[j].y2) {
                    intersections.push_back({edges[i].x1, edges[i].y1});
                }
                if (edges[i].x2 == edges[j].x1 && edges[i].y2 == edges[j].y1) {
                    intersections.push_back({edges[i].x2, edges[i].y2});
                }
                if (edges[i].x2 == edges[j].x2 && edges[i].y2 == edges[j].y2) {
                    intersections.push_back({edges[i].x2, edges[i].y2});
                }
            }
        }

        sort(intersections.begin(), intersections.end(), [](const Intersection& a, const Intersection& b) {
            return a.x < b.x || (a.x == b.x && a.y < b.y);
        });

        vector<pair<int, int>> graph;
        for (int i = 0; i < intersections.size(); i++) {
            graph.push_back({intersections[i].x, intersections[i].y});
        }

        vector<int> distance(graph.size(), 1e9);
        vector<int> parent(graph.size(), -1);
        priority_queue<pair<int, int>, vector<pair<int, int>>, Compare> pq;
        pq.push({0, 0});
        distance[0] = 0;

        while (!pq.empty()) {
            int u = pq.top().second;
            pq.pop();

            if (distance[u] < 0) continue;

            for (int i = 0; i < graph.size(); i++) {
                if (graph[i].first == graph[u].first && graph[i].second == graph[u].second) continue;

                int v = i;
                int d = distance[u] + abs(graph[v].first - graph[u].first) + abs(graph[v].second - graph[u].second);

                if (d < distance[v]) {
                    distance[v] = d;
                    parent[v] = u;
                    pq.push({-d, v});
                }
            }
        }

        if (distance[graph.size() - 1] == 1e9) {
            cout << -1 << endl;
        } else {
            vector<pair<int, int>> path;
            int u = graph.size() - 1;
            while (u != -1) {
                path.push_back(graph[u]);
                u = parent[u];
            }

            reverse(path.begin(), path.end());

            for (int i = 0; i < path.size(); i++) {
                cout << path[i].first << " " << path[i].second << endl;
            }
        }

        cout << 0 << endl;
    }

    return 0;
}
