#include <iostream>
#include <vector>
#include <queue>
#include <limits>

using namespace std;

struct Edge {
    int u, v, w;
};

struct Compare {
    bool operator()(const pair<int, int>& a, const pair<int, int>& b) {
        return a.second > b.second;
    }
};

int find(vector<int>& parent, int i) {
    if (parent[i] == i) return i;
    return parent[i] = find(parent, parent[i]);
}

void unionSet(vector<int>& parent, vector<int>& rank, int x, int y) {
    int xRoot = find(parent, x);
    int yRoot = find(parent, y);

    if (xRoot == yRoot) return;

    if (rank[xRoot] < rank[yRoot]) {
        parent[xRoot] = yRoot;
    } else if (rank[xRoot] > rank[yRoot]) {
        parent[yRoot] = xRoot;
    } else {
        parent[yRoot] = xRoot;
        rank[xRoot]++;
    }
}

int kruskal(vector<Edge>& edges, int n) {
    vector<int> parent(n), rank(n);
    for (int i = 0; i < n; i++) {
        parent[i] = i;
    }

    priority_queue<pair<int, int>, vector<pair<int, int>>, Compare> pq;
    for (const auto& edge : edges) {
        pq.push({edge.w, edge.u});
    }

    int mstWeight = 0;
    while (!pq.empty()) {
        int w = pq.top().first;
        int u = pq.top().second;
        pq.pop();

        int v = find(parent, u);
        if (v == 0) break;

        unionSet(parent, rank, u, v);
        mstWeight = max(mstWeight, w);
    }

    if (find(parent, 0) != 0) return -1;

    vector<int> weights;
    for (const auto& edge : edges) {
        if (find(parent, edge.u) == find(parent, edge.v)) {
            weights.push_back(edge.w);
        }
    }

    if (weights.empty()) return -1;

    int minWeight = *min_element(weights.begin(), weights.end());
    int maxWeight = *max_element(weights.begin(), weights.end());
    return maxWeight - minWeight;
}

int main() {
    int n, m;
    while (cin >> n >> m) {
        if (n == 0 && m == 0) break;

        vector<Edge> edges;
        for (int i = 0; i < m; i++) {
            int u, v, w;
            cin >> u >> v >> w;
            edges.push_back({u, v, w});
        }

        cout << kruskal(edges, n) << endl;
    }

    return 0;
}
