#include <iostream>
#include <vector>
#include <string>
#include <sstream>

int main() {
    std::string line;
    while (std::getline(std::cin, line)) {
        if (line == "0") break;

        int n;
        std::istringstream iss(line);
        iss >> n;

        std::vector<std::vector<int>> table(n, std::vector<int>(n));
        for (int i = 0; i < n; ++i) {
            std::getline(std::cin, line);
            std::istringstream iss(line);
            for (int j = 0; j < n; ++j) {
                iss >> table[i][j];
            }
        }

        // Calculate row sums
        for (int i = 0; i < n; ++i) {
            int sum = 0;
            for (int j = 0; j < n; ++j) {
                sum += table[i][j];
            }
            table[i].push_back(sum);
        }

        // Calculate column sums
        for (int j = 0; j < n; ++j) {
            int sum = 0;
            for (int i = 0; i < n; ++i) {
                sum += table[i][j];
            }
            for (int i = 0; i < n; ++i) {
                table[i].push_back(sum);
            }
        }

        // Print table
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < n * 2; ++j) {
                std::cout << std::setw(5) << table[i][j];
            }
            std::cout << std::endl;
        }

        std::cout << std::endl;
    }

    return 0;
}
