import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.*;

public class Main {
    static class Edge implements Comparable<Edge> {
        int v, w;

        public Edge(int v, int w) {
            this.v = v;
            this.w = w;
        }

        @Override
        public int compareTo(Edge o) {
            return Integer.compare(this.w, o.w);
        }
    }

    static int find(int[] parent, int i) {
        if (parent[i] != i) {
            parent[i] = find(parent, parent[i]);
        }
        return parent[i];
    }

    static void union(int[] parent, int[] rank, int x, int y) {
        int rootX = find(parent, x);
        int rootY = find(parent, y);

        if (rootX != rootY) {
            if (rank[rootX] > rank[rootY]) {
                parent[rootY] = rootX;
            } else if (rank[rootX] < rank[rootY]) {
                parent[rootX] = rootY;
            } else {
                parent[rootY] = rootX;
                rank[rootX]++;
            }
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String[] input;
        while ((input = br.readLine().split(" ")) != null && input.length > 0 && input[0].equals("0")) {
            input = br.readLine().split(" ");
            int n = Integer.parseInt(input[0]);
            int m = Integer.parseInt(input[1]);
            Edge[] edges = new Edge[m];
            for (int i = 0; i < m; i++) {
                input = br.readLine().split(" ");
                edges[i] = new Edge(Integer.parseInt(input[0]) - 1, Integer.parseInt(input[1]) - 1, Integer.parseInt(input[2]));
            }
            Arrays.sort(edges);
            int[] parent = new int[n];
            int[] rank = new int[n];
            for (int i = 0; i < n; i++) {
                parent[i] = i;
                rank[i] = 0;
            }
            int[] result = new int[m];
            int j = 0;
            for (int i = 0; i < m; i++) {
                if (find(parent, edges[i].v) != find(parent, edges[i].w)) {
                    union(parent, rank, edges[i].v, edges[i].w);
                    result[j++] = edges[i].w;
                }
            }
            if (j < n - 1) {
                System.out.println(-1);
            } else {
                int min = Integer.MAX_VALUE;
                int max = Integer.MIN_VALUE;
                for (int i = 0; i < j; i++) {
                    min = Math.min(min, result[i]);
                    max = Math.max(max, result[i]);
                }
                System.out.println(max - min);
            }
        }
    }
}
