#include <iostream>
#include <vector>
#include <queue>
#include <string>
#include <algorithm>

using namespace std;

const int dx[4] = {0, 1, 0, -1};
const int dy[4] = {1, 0, -1, 0};

int bfs(vector<vector<char>>& map, vector<vector<int>>& dist, int x, int y) {
    queue<pair<int, int>> q;
    q.push({x, y});
    dist[x][y] = 0;
    while (!q.empty()) {
        int cx = q.front().first;
        int cy = q.front().second;
        q.pop();
        for (int i = 0; i < 4; i++) {
            int nx = cx + dx[i];
            int ny = cy + dy[i];
            if (nx >= 0 && nx < map.size() && ny >= 0 && ny < map[0].size() && map[nx][ny] != '#' && dist[nx][ny] == -1) {
                dist[nx][ny] = dist[cx][cy] + 1;
                q.push({nx, ny});
            }
        }
    }
    return dist[x][y];
}

int main() {
    int t;
    cin >> t;
    while (t--) {
        int w, h, n;
        cin >> w >> h >> n;
        vector<vector<char>> map(h, vector<char>(w));
        vector<vector<int>> dist(h, vector<int>(w, -1));
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                cin >> map[i][j];
            }
        }
        int ans = 0;
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                if (map[i][j] >= 'a' && map[i][j] <= 'z') {
                    int x = i, y = j;
                    while (dist[x][y] == -1) {
                        dist[x][y] = bfs(map, dist, x, y);
                        ans = max(ans, dist[x][y]);
                    }
                }
            }
        }
        cout << ans << endl;
    }
    return 0;
}
