import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.*;

public class Main {
    static class Pair {
        int x, y;

        public Pair(int x, int y) {
            this.x = x;
            this.y = y;
        }
    }

    static int[][] dirs = {{0, 1}, {0, -1}, {1, 0}, {-1, 0}};

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int T = 1;
        while (T-- > 0) {
            StringTokenizer st = new StringTokenizer(br.readLine());
            int w = Integer.parseInt(st.nextToken());
            int h = Integer.parseInt(st.nextToken());
            int n = Integer.parseInt(st.nextToken());
            char[][] map = new char[h][];
            for (int i = 0; i < h; i++) {
                map[i] = br.readLine().toCharArray();
            }
            int[][] ghost = new int[n][2];
            int[][] target = new int[n][2];
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < h; j++) {
                    for (int k = 0; k < w; k++) {
                        if (map[j][k] == (char) ('a' + i)) {
                            ghost[i][0] = j;
                            ghost[i][1] = k;
                        } else if (map[j][k] == (char) ('A' + i)) {
                            target[i][0] = j;
                            target[i][1] = k;
                        }
                    }
                }
            }
            int ans = 0;
            while (true) {
                boolean changed = false;
                for (int i = 0; i < n; i++) {
                    if (ghost[i][0] != target[i][0] || ghost[i][1] != target[i][1]) {
                        changed = true;
                        int[] dist = bfs(map, ghost[i][0], ghost[i][1], target[i][0], target[i][1]);
                        if (dist[0] == -1) {
                            System.out.println(-1);
                            return;
                        }
                        ans += dist[0];
                        ghost[i][0] = target[i][0];
                        ghost[i][1] = target[i][1];
                    }
                }
                if (!changed) break;
            }
            System.out.println(ans);
        }
    }

    static int[] bfs(char[][] map, int x, int y, int tx, int ty) {
        int w = map[0].length;
        int h = map.length;
        boolean[][] visited = new boolean[h][w];
        Queue<Pair> q = new LinkedList<>();
        q.offer(new Pair(x, y));
        visited[x][y] = true;
        int[] dist = new int[]{0, -1};
        while (!q.isEmpty()) {
            Pair p = q.poll();
            if (p.x == tx && p.y == ty) return dist;
            for (int[] dir : dirs) {
                int nx = p.x + dir[0];
                int ny = p.y + dir[1];
                if (nx >= 0 && nx < h && ny >= 0 && ny < w && map[nx][ny] != '#' && !visited[nx][ny]) {
                    q.offer(new Pair(nx, ny));
                    visited[nx][ny] = true;
                    dist[0]++;
                }
            }
        }
        return dist;
    }
}
