import java.util.Scanner;
import java.util.HashSet;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            String line = scanner.nextLine();
            if (line.equals(".")) break;
            HashSet<String> arrays = new HashSet<>();
            HashSet<String> assigned = new HashSet<>();
            int lineNumber = 1;
            while (line != null && !line.equals(".")) {
                line = scanner.nextLine();
                if (line.equals(".")) break;
                if (line.contains("=")) {
                    String[] parts = line.split("=");
                    String arrayName = parts[0].trim();
                    String index = parts[1].trim();
                    if (!arrays.contains(arrayName)) {
                        System.out.println(lineNumber);
                        break;
                    }
                    if (index.contains("[") && index.contains("]")) {
                        String value = index.substring(index.indexOf("[") + 1, index.indexOf("]"));
                        if (!assigned.contains(value)) {
                            System.out.println(lineNumber);
                            break;
                        }
                        if (Integer.parseInt(value) >= Integer.parseInt(arrayName.substring(arrayName.indexOf("[") + 1, arrayName.indexOf("]")))) {
                            System.out.println(lineNumber);
                            break;
                        }
                    } else if (!assigned.contains(index)) {
                        System.out.println(lineNumber);
                        break;
                    }
                    assigned.add(index);
                } else {
                    String arrayName = line.trim();
                    arrays.add(arrayName);
                }
                lineNumber++;
            }
        }
    }
}
