#include <stdio.h>
#include <math.h>

typedef struct {
    double x, y;
} Point;

double distance(Point p1, Point p2) {
    return sqrt(pow(p1.x - p2.x, 2) + pow(p1.y - p2.y, 2));
}

double max_distance(Point p, Point *points, int n) {
    double max = 0;
    for (int i = 0; i < n; i++) {
        double d = distance(p, points[i]);
        if (d > max) max = d;
    }
    return max;
}

int main() {
    int n;
    while (scanf("%d", &n) == 1) {
        if (n == 0) break;
        Point points[n];
        for (int i = 0; i < n; i++) {
            scanf("%lf %lf", &points[i].x, &points[i].y);
        }
        Point center = {points[0].x, points[0].y};
        for (int i = 0; i < n; i++) {
            center.x += points[i].x;
            center.y += points[i].y;
        }
        center.x /= n;
        center.y /= n;
        double max = 0;
        for (int i = 0; i < n; i++) {
            double d = distance(center, points[i]);
            if (d > max) max = d;
        }
        printf("%.10lf\n", max);
    }
    return 0;
}
