#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>

struct Point {
    double x, y;
};

double distance(const Point& p1, const Point& p2) {
    return std::sqrt(std::pow(p2.x - p1.x, 2) + std::pow(p2.y - p1.y, 2));
}

double maxDistance(const std::vector<Point>& points) {
    double maxDist = 0;
    for (int i = 0; i < points.size(); ++i) {
        double dist = 0;
        for (int j = 0; j < points.size(); ++j) {
            if (i != j) {
                dist = std::max(dist, distance(points[i], points[j]));
            }
        }
        maxDist = std::max(maxDist, dist);
    }
    return maxDist;
}

int main() {
    int n;
    while (std::cin >> n && n != 0) {
        std::vector<Point> points(n);
        for (int i = 0; i < n; ++i) {
            std::cin >> points[i].x >> points[i].y;
        }
        std::cout << std::fixed << std::setprecision(10) << maxDistance(points) << std::endl;
    }
    return 0;
}
