import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int n = scanner.nextInt();
            if (n == 0) break;
            int[][] points = new int[n][2];
            for (int i = 0; i < n; i++) {
                points[i][0] = scanner.nextInt();
                points[i][1] = scanner.nextInt();
            }
            double maxDistance = 0;
            for (int i = 0; i < n; i++) {
                double distance = getDistance(points, i);
                if (distance > maxDistance) maxDistance = distance;
            }
            System.out.printf("%.10f%n", maxDistance);
        }
    }

    public static double getDistance(int[][] points, int index) {
        int x1 = points[index][0];
        int y1 = points[index][1];
        int x2 = points[(index + 1) % points.length][0];
        int y2 = points[(index + 1) % points.length][1];
        int x3 = points[0][0];
        int y3 = points[0][1];
        double a = (x2 - x1) * (y3 - y1) - (y2 - y1) * (x3 - x1);
        double b = (x3 - x1) * (y2 - y1) - (y3 - y1) * (x2 - x1);
        double c = (x2 - x1) * (x3 - x1) + (y2 - y1) * (y3 - y1);
        double distance = Math.abs(a / Math.sqrt(a * a + b * b)) * Math.sqrt(c / c);
        return distance;
    }
}
